import React, { Component } from 'react';
import {
    StyleSheet,
    Text,
    View,
    Image,
    TouchableOpacity,
    NavigatorIOS,
    Navigator,
    ScrollView,
    AlertIOS,
    AsyncStorage,
} from 'react-native';
import Help from './help';
import Security from './security/security';
import Terms from './terms';
import Feedback from './feedback';

import NavigationBar from '../common/navBar';
import loginPage from '../loginPage'
import Util from '../common/util';

var userIcon = 'data:image/png;base64,';
var helpIcon = 'data:image/png;base64,';
var termIcon = 'data:image/png;base64,';
var callUsIcon = 'data:image/png;base64,';
var feedbackIcon = 'data:image/png;base64,';
var moreIcon = 'data:image/png;base64,';


export default class settingPage extends Component {
    constructor(props){
        super(props);
    }

    render() {
        return (
            <View style={{flex:1,backgroundColor: '#f3f4f9',}}>
                <NavigationBar title={'设置'}/>
                <ScrollView>
                    <View style={styles.container}>
                        <View style={styles.logoContainer}>
                            <Image source={{url:"http://localhost:8888/tower_crane/Application/Home/View/towerCrane/images/logoIcon.png"}}
                                   style={styles.logoImage}
                                   resizeMode='contain'/>
                        </View>
                        <View style={{justifyContent:'center', alignItems: 'center',marginTop:10,marginBottom:20}}>
                            <Text style={styles.versionText}>版本号1.0.0</Text>
                        </View>
                        <View style={styles.itemContainer}>
                            <TouchableOpacity
                                onPress={this._goPage.bind(this,Security,'账户与安全')}>
                                <View style={styles.item}>
                                    <Image
                                        style={styles.img}
                                        source={{uri: userIcon}}
                                    />
                                    <Text style={styles.text}>账户安全</Text>
                                    <Image
                                        style={styles.moreIcon}
                                        source={{uri: moreIcon}}
                                    />
                                </View>
                            </TouchableOpacity>

                            <TouchableOpacity
                                onPress={this._goPage.bind(this,Help,'帮助中心')}>
                                <View style={styles.item}>
                                    <Image
                                        style={styles.img}
                                        source={{uri: helpIcon}}
                                    />
                                    <Text style={styles.text}>帮助中心</Text>
                                    <Image
                                        style={styles.moreIcon}
                                        source={{uri: moreIcon}}
                                    />
                                </View>
                            </TouchableOpacity>

                            <TouchableOpacity
                                onPress={this._goPage.bind(this,Terms,'服务条款')}>
                                <View style={styles.item}>
                                    <Image
                                        style={styles.img}
                                        source={{uri: termIcon}}
                                    />
                                    <Text style={styles.text}>服务条款</Text>
                                    <Image
                                        style={styles.moreIcon}
                                        source={{uri: moreIcon}}
                                    />
                                </View>
                            </TouchableOpacity>

                            <TouchableOpacity
                                onPress={this._goPage.bind(this,Feedback,'意见反馈')}>
                                <View style={styles.item}>
                                    <Image
                                        style={styles.img}
                                        source={{uri: feedbackIcon}}
                                    />
                                    <Text style={styles.text}>意见反馈</Text>
                                    <Image
                                        style={styles.moreIcon}
                                        source={{uri: moreIcon}}
                                    />
                                </View>
                            </TouchableOpacity>

                            <TouchableOpacity
                                onPress={this._showAbout.bind(this)}>
                                <View style={styles.item}>
                                    <Image
                                        style={styles.img}
                                        source={{uri: callUsIcon}}
                                    />
                                    <Text style={styles.text}>联系我们</Text>
                                    <Image
                                        style={styles.moreIcon}
                                        source={{uri: moreIcon}}
                                    />
                                </View>
                            </TouchableOpacity>

                            <TouchableOpacity
                                onPress={this._logout.bind(this)}>
                                <View style={styles.logout}>
                                    <Text style={styles.logoutText}>退出登陆</Text>
                                </View>
                            </TouchableOpacity>
                        </View>
                    </View>
                </ScrollView>
            </View>
        );
    }

    _goPage(component,title) {
        // 从storageData中取得存储的username,传递给下个页面
        storage.load({
            key: 'storageData',
            autoSync: true,
            syncInBackground: true,
        }).then(ret => {
            const { navigator } = this.props;
            if(navigator) {
                navigator.push({
                    name: title,
                    component: component,
                    params: {
                        username: JSON.parse(ret.username),
                    }
                })
            }
        })

    }

    _showAbout(){
        AlertIOS.alert('如有问题请致电', '18673241234', [{text: '确认'}]);
    }

    _logout(){
        AlertIOS.alert('确定退出?','', [{text: '取消'}, {text: '确定',
            onPress: ()=>{
                // 清除后端cookie
                let that = this;
                let formData = new FormData();
                formData.append("action",'clearCookie');
                let url = "http://localhost:8888/tower_crane/index.php/Home/towerCrane/logout";
                Util.post(url, formData,
                    function(responseJson) {
                        if(responseJson.status === 1) {
                            // 清理掉storageData中的数据
                            storage.remove({
                                key: 'storageData'
                            }).
                            then(()=>{
                                // 跳转到登录页面
                                const { navigator } = that.props;
                                if(navigator) {
                                    navigator.popToTop();
                                    navigator.push({
                                        name: '登录页',
                                        component: loginPage,
                                    })
                                }
                            });
                        }
                    },
                    function(err){
                        AlertIOS.alert('失败！', err, [{text: '确认'}]);
                    });
                },}
            ]);
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        marginTop: 30,
    },
    logoContainer: {
        alignItems: 'center',
    },
    logoImage: {
        width: 100,
        height: 100,
    },
    versionText: {
        fontSize: 16,
        fontWeight: '300',
        color: '#666',
    },
    img: {
        flex: 1,
        width: 25,
        height: 28
    },
    itemContainer: {
        backgroundColor: '#fff',
        borderTopWidth: Util.pixel,
        borderBottomWidth: Util.pixel,
        borderColor: 'rgba(0,0,0,0.1)',
    },
    item: {
        paddingLeft: 15,
        paddingRight: 15,
        flexDirection: 'row',
        height: 50,
        borderBottomWidth: Util.pixel,
        borderColor: 'rgba(0,0,0,0.1)',
        alignItems: 'center',
    },
    text: {
        flex: 10,
        marginLeft: 15,
        fontSize: 18,
        fontWeight: '300',
        color: '#333',
    },
    moreIcon: {
        flex: 1,
        width: 15,
        height: 15,
    },
    logout: {
        borderBottomWidth: Util.pixel,
        height: 50,
        borderColor: '#efefef',
        alignItems: 'center',
        justifyContent: 'center',
    },
    logoutText: {
        fontSize: 18,
        fontWeight: '300',
        color: '#DC143C',
    }

});

module.exports = settingPage;